itle.\'</figcaption>\r\n					</figure>\';\r\n\r\n				}\r\n			}\r\n\r\n		echo \'\r\n		</div>\r\n	</div>\r\n	<div class=\"clearfix\"></div>\r\n	<!-- end gallery -->\';\r\n}\r\n?>\r\n','<!-- *******************************************************\r\nGALERIE\r\n******************************************************** -->\r\n\r\n<fieldset class=\"form-horizontal\">\r\n    <legend>Galerie</legend>\r\n\r\n    <div class=\"form-group\">\r\n        <label class=\"col-sm-2 control-label\" for=\"headline\">Bilder</label>\r\n		<div class=\"col-sm-10\">\r\n            REX_MEDIALIST[id=\"1\" widget=\"1\"]\r\n        </div>\r\n    </div>\r\n\r\n	<div class=\"form-group\">\r\n		<label class=\"col-sm-2 control-label\" for=\"size\">Bilder pro Zeile</label>\r\n		<div class=\"col-sm-10\">\r\n			<?php\r\n				$options = array(\r\n				\'2\'=>\'2\',\r\n				\'3\'=>\'3\',\r\n				\'4\'=>\'4\'\r\n			);\r\n			?>\r\n			<select name=\"REX_INPUT_VALUE[1]\" id=\"size\" class=\"form-control\">\r\n			<?php foreach ($options as $k=>$v) : ?>\r\n			<option value=\"<?php echo $k; ?>\"<?php if ($k == \"REX_VALUE[1]\") echo \' selected=\"selected\"\' ?>><?php echo $v; ?></option>\r\n			<?php endforeach ?>\r\n			</select>\r\n		</div>\r\n	</div>\r\n</fieldset>\r\n','2020-02-21 19:50:51','admin','0000-00-00 00:00:00','',NULL,0),
