<?php echo rex_url::base(\'resources/js/photo_swipe/photoswipe.min.js\') ?>\"></script>\r\n<script src=\"<?php echo rex_url::base(\'resources/js/photo_swipe/photoswipe-ui-default.min.js\') ?>\"></script>\r\n<script src=\"<?php echo rex_url::base(\'resources/js/photo_swipe/init-gallery.js\') ?>\"></script>\r\n\r\n<script type=\"text/javascript\" src=\"<?php echo rex_url::base(\'resources/js/rex-com-demo.js\') ?>\"></script>\r\n\r\n</body>\r\n</html>\r\n',0,'2020-02-21 17:41:25','{\"ctype\":[],\"modules\":{\"1\":{\"all\":\"1\"}},\"categories\":{\"all\":\"1\"}}',0,'admin','2020-02-21 22:56:15','admin'),
  (6,NULL,'00 . Header','<?php\r\n$ycom_user = rex_ycom_auth::getUser();\r\n\r\n// error_reporting(0);\r\nerror_reporting(E_ALL);\r\n// Das Error-Reporting sollte nur während der Entwicklung angeschaltet sein. Bei einer Live-Website sollte es auf 0 gestellt werden.\r\n\r\nheader(\'Content-Type: text/html; charset=utf-8\');\r\n?><!DOCTYPE html>\r\n<html lang=\"<?php echo rex_clang::getCurrent()->getCode(); ?>\">\r\n<head>\r\n	<meta charset=\"utf-8\">\r\n	<meta http-equiv=\"X-UA-Compatible\" content=\"IE=edge\">\r\n	<meta name=\"viewport\" content=\"width=device-width, initial-scale=1\">\r\n	<?php\r\n	// Als Title-Tag den Artikelnamen, außer er wird manuell gesetzt\r\n	if ($this->getValue(\"art_title\") != \"\") {\r\n		$title = htmlspecialchars($this->getValue(\'art_title\'));\r\n	} else {\r\n		$title = htmlspecialchars($this->getValue(\'name\'));\r\n	}\r\n\r\n	echo \'\r\n	<title>\'.$title.\'</title>\';\r\n\r\n	// Keywords and description\r\n	// Wenn der aktuelle Artikel kein Keyword und Description besitzt, werden die aus dem Startartikel genommen\r\n	if ($this->getValue(\"art_keywords\") != \"\") {\r\n		$keywords = $this->getValue(\"art_keywords\");\r\n	} else {\r\n		$home = new rex_article_content(rex_article::getSiteStartArticleId());\r\n		$keywords = $home->getValue(\'art_keywords\');\r\n	}\r\n\r\n	if ($this->getValue(\"art_description\") != \"\") {\r\n		$description = $this->getValue(\"art_description\");\r\n	} else {\r\n		$home = new rex_article_content(rex_article::getSiteStartArticleId());\r\n		$description = $home->getValue(\'art_description\');\r\n	}\r\n\r\n	echo \'\r\n	<meta name=\"keywords\" content=\"\'.htmlspecialchars($keywords).\'\">\';\r\n\r\n	echo \'\r\n	<meta name=\"description\" content=\"\'.htmlspecialchars($description).\'\">\';\r\n	?>\r\n\r\n	<link rel=\"stylesheet\" href=\"<?php echo rex_url::base(\'resources/css/bootstrap.min.css\') ?>\">\r\n	<link rel=\"stylesheet\" href=\"<?php echo rex_url::base(\'resources/css/base.css\') ?>\">\r\n	<link rel=\"stylesheet\" href=\"<?php echo rex_url::base(\'resources/css/rex-com-demo.css\') ?>\">\r\n	<link rel=\"stylesheet\" href=\"<?php echo rex_url::base(\'resources/css/fonts.css\') ?>\">\r\n	<link rel=\"stylesheet\" href=\"<?php echo rex_url::base(\'resources/css/font-awesome.min.css\') ?>\">\r\n	<link rel=\"stylesheet\" href=\"<?php echo rex_url::base(\'resources/css/themify-icons.css\') ?>\">\r\n\r\n	<!-- HTML5 shim and Respond.js IE8 support of HTML5 elements and media queries -->\r\n	<!--[if lt IE 9]>\r\n		<script src=\"http://oss.maxcdn.com/libs/html5shiv/3.7.0/html5shiv.js\"></script>\r\n		<script src=\"http://oss.maxcdn.com/libs/respond.js/1.4.2/respond.min.js\"></script>\r\n	<![endif]-->\r\n	<script type=\"text/javascript\" src=\"<?php echo rex_url::base(\'resources/js/jquery.js\') ?>\"></script>\r\n</head>\r\n',0,'2020-02-21 17:42:26','{\"ctype\":[],\"modules\":{\"1\":{\"all\":\"1\"}},\"categories\":{\"all\":\"1\"}}',0,'admin','2020-02-21 17:42:26','admin'),
  (7,NULL,'06 . Headerbild','<?php\r\n// Headerbild rekursiv einbinden.\r\n// Wenn der aktuelle Artikel kein Bild hat, wird das aus den Elternkategorien genommen.\r\n$header = \'\';\r\n$headerpic = $this->getValue(\'art_file\');\r\n\r\nif ($headerpic == \'\') {\r\n    $category = rex_article::getCurrent()->getCategory();\r\n    while ($header == \'\') {\r\n        if ($category == null) {\r\n            break;\r\n        }\r\n        $headerpic = $category->getValue(\'art_file\');\r\n        $category = $category->getParent();\r\n    } \r\n}\r\n\r\nif ($headerpic == \'\') {\r\n    $headerpic = rex_article::getSiteStartArticle()->getValue(\'art_file\');\r\n}\r\n\r\n\r\n$header = \'\r\n<div class=\"header\" style=\"background-image: url(\'.rex_url::base(\'index.php?rex_media_type=header&rex_media_file=\'.$headerpic).\');\r\n    \">\r\n	<div class=\"filter\"></div>\r\n	<div class=\"container text-center\">\r\n		<h1>\'.$this->getValue(\'name\').\'</h1>\';\r\n		if ($this->getValue(\'art_subtitle\') != \'\') {\r\n			$header .= \'\r\n			<h3>\'.$this->getValue(\'art_subtitle\').\'</h3>\';\r\n		}\r\n	$header .= \' \r\n	</div>\r\n</div>\';\r\n\r\nif (SITE_TYPE == \'col1\' || SITE_TYPE == \'col2\') {\r\necho $header;\r\n}\r\n?>\r\n',0,'2020-02-21 17:42:50','{\"ctype\":[],\"modules\":{\"1\":{\"all\":\"1\"}},\"categories\":{\"all\":\"1\"}}',0,'admin','0000-00-00 00:00:00',''),
  (8,NULL,'07 . Navigation','<?php\r\n$path = explode(\"|\",$this->getValue(\"path\").$this->getValue(\"article_id\").\"|\");\r\n$path1 = ((!empty($path[1])) ? $path[1] : \'\');\r\n$path2 = ((!empty($path[2])) ? $path[2] : \'\');\r\n\r\n// Gruppen-Berechtigung eines YCom-Nutzers auslesen\r\n$ycom_user = rex_ycom_auth::getUser();\r\n$nav_main = \'\';\r\n\r\nforeach (rex_category::getRootCategories(true) as $lev1) { // wenn true, dann werden nur die Artikel mit Status online berücksichtigt\r\n\r\n	if (rex_article::get($lev1->getId())->isPermitted() && $lev1->getId() != rex_article::getSiteStartArticleId()) { // Home soll nicht in der Navi auftauchen\r\n		\r\n		if ($lev1->getId() == 4) { // falls Profilkategorie, dann Profil-Foto\r\n			$profile_pic = \'profile_default.png\';\r\n			if ($ycom_user && rex_ycom_auth::getUser()->getValue(\'pic\') != \'\') $profile_pic = rex_ycom_auth::getUser()->getValue(\'pic\'); // prüfen, ob User eingeloggt ist und ob er ein Bild bhinterlegt hat\r\n			$profilelink = \'\r\n			<a href=\"#\" class=\"profile-photo dropdown-toggle\" data-toggle=\"dropdown\">\r\n                <div class=\"profile-photo-small\">\r\n                    <img src=\"index.php?rex_media_type=profile&rex_media_file=\'.$profile_pic.\'\" alt=\"Profilbild\" class=\"img-circle img-responsive img-no-padding\">\r\n                </div>\r\n            </a>\';\r\n		} else {\r\n			$profilelink = \'\r\n			<a class=\"dropdown-toggle\" href=\"\'.$lev1->getUrl().\'\">\'.htmlspecialchars($lev1->getValue(\'name\')).\'</a>\';\r\n		}\r\n\r\n		if ($lev1->getId() == $path1) {\r\n			$nav_main .= \'\r\n			<li class=\"dropdown active\">\'.$profilelink;\r\n		} else {\r\n   			$nav_main .= \'\r\n			<li class=\"dropdown\">\'.$profilelink;\r\n		}\r\n\r\n		// 1st level start\r\n		$lev1Size = sizeof($lev1->getChildren(true));\r\n\r\n			if ($lev1Size != \"0\") {\r\n\r\n				$nav_main .= \'\r\n				<ul class=\"dropdown-menu dropdown-menu-right\">\';\r\n\r\n					// START 2nd level categories\r\n					foreach ($lev1->getChildren(true) as $lev2):\r\n						if (rex_article::get($lev2->getId())->isPermitted()) {\r\n\r\n							$nav_icon = ($lev2->getValue(\'art_nav_icon\') != \'\') ? \'<span class=\"icon-simple\"><i class=\"\'.$lev2->getValue(\'art_nav_icon\').\'\"></i></span> \' : \'\';\r\n\r\n							if ($lev2->getId() == $path2) {\r\n								$nav_main .= \'\r\n								<li class=\"dropdown-toggle active\"><a href=\"\'.$lev2->getUrl().\'\">\'.$nav_icon.htmlspecialchars($lev2->getValue(\'name\')).\'</a></li>\';\r\n							} else {\r\n								$nav_main .= \'\r\n								<li class=\"dropdown-toggle\"><a href=\"\'.$lev2->getUrl().\'\">\'.$nav_icon.htmlspecialchars($lev2->getValue(\'name\')).\'</a></li>\';\r\n							}\r\n						}\r\n					endforeach;\r\n\r\n				$nav_main .= \'\r\n				</ul>\';\r\n\r\n			}\r\n\r\n		$nav_main .= \'\r\n		</li>\';\r\n	}\r\n}\r\n\r\necho \'\r\n<ul class=\"nav navbar-nav navbar-right\">\r\n	\'.$nav_main.\'\r\n</ul>\';\r\n?>\r\n',0,'2020-02-22 20:33:31','{\"ctype\":[],\"modules\":{\"1\":{\"all\":\"1\"}},\"categories\":{\"all\":\"1\"}}',0,'admin','2020-02-22 20:33:31','admin'),
  (9,NULL,'08 . Subnavigation (Seitenspalte)','<?php\r\n$path = explode(\"|\",$this->getValue(\"path\").$this->getValue(\"article_id\").\"|\");\r\n$path1 = ((!empty($path[1])) ? $path[1] : \'\');\r\n$path2 = ((!empty($path[2])) ? $path[2] : \'\');\r\n\r\n$article = rex_article::get($path1);\r\n$articleUrl = $article->getUrl();\r\n$articleName = $article->getName();\r\n\r\n$nav = rex_navigation::factory();\r\n$nav->addCallback(\'rex_ycom_auth::articleIsPermitted\');\r\n$subnav = $nav->get($path1,2,TRUE,TRUE);\r\n\r\nif ($subnav != \'\') {\r\n\r\n	echo \'\r\n	<h4 id=\"sidenav_title\"><a href=\"\'.$articleUrl.\'\">\'.$articleName.\'</a></h4>\';\r\n\r\n	echo \'\r\n	<div class=\"subnav\">\r\n	\'.$subnav.\'\r\n	</div\';\r\n\r\n}\r\n?>\r\n',0,'2020-02-22 14:09:29','{\"ctype\":[],\"modules\":{\"1\":{\"all\":\"1\"}},\"categories\":{\"all\":\"1\"}}',0,'admin','2020-02-22 14:09:29','admin'),
